import React, { useState } from 'react';
import '../Navbar.css';
import Navbar from '../LightNavbar';
import Header from '../cards light/Header';
import ExperienceCardHome from '../cards light/Experience card short';
import EducationCard from '../cards light/Education card';
import AboutCard from '../cards light/About card';
import { makeStyles } from '@material-ui/core/styles';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import Card from 'react-bootstrap/Card'
import { Link } from 'react-router-dom';
import closeMobileMenu from '../Navbar';

// import LightMode from './LightMode'

const styles = makeStyles({
    wrapper: {
        width: "65%",
        margin: "auto",
        textAlign: "left"
    },
})

function Home() {
    const classes = styles();

    return (

        <div className="App">
        <Navbar />

            <div className={classes.wrapper}>

                <Header />
                <AboutCard />
                <ExperienceCardHome />
                <Card 
                className={classes.smallSpace}>

                <li >
                <div style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
                    <Link
                        to='/experience'
                        onClick={closeMobileMenu}
                    >
                        Show all experiences â†’ 

                    </Link>
                </div>

                </li>

                <div 
                className={classes.largeSpace}>
                </div>
                </Card>
                <EducationCard />

                <div className={classes.largeSpace}>
                </div>
            </div>

        </div>
    );
}

export default Home;
